// Dashboard.js
import React, { useEffect, useState } from 'react';
import { Container, Row, Col, Card, Table, Button } from 'react-bootstrap';
import axios from './mockApi'; // Import axios from mockapi.js

const Dashboard = () => {
  // State to hold fetched muddamaal data
  const [muddamaalList, setMuddamaalList] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch data on component mount
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('/api/muddamaal'); // Fetch from the mock API
        setMuddamaalList(response.data); // Set the fetched data
        setLoading(false); // Stop loading once data is fetched
      } catch (error) {
        console.error('Error fetching muddamaal data:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  return (
    <Container fluid>
      <Row>
        <Col>
          <h1 className="my-4">Muddamaal Dashboard</h1>
        </Col>
      </Row>

      <Row>
        <Col>
          {loading ? (
            <p>Loading data...</p>
          ) : (
            <Table striped bordered hover>
              <thead>
                <tr>
                  <th>#</th>
                  <th>Crime No</th>
                  <th>Crime Date</th>
                  <th>Muddamaal No</th>
                  <th>Category</th>
                  <th>Quantity</th>
                  <th>Value</th>
                  <th>Status</th>
                  <th>Custody Date</th>
                </tr>
              </thead>
              <tbody>
                {muddamaalList.map((item, index) => (
                  <tr key={item.id}>
                    <td>{index + 1}</td>
                    <td>{item.crimeNo}</td>
                    <td>{item.crimeDate}</td>
                    <td>{item.muddamaalNo}</td>
                    <td>{item.category}</td>
                    <td>{item.quantity}</td>
                    <td>â‚¹{item.value}</td>
                    <td>{item.status}</td>
                    <td>{item.custodyDate}</td>
                  </tr>
                ))}
              </tbody>
            </Table>
          )}
        </Col>
      </Row>

      <Row className="mt-4">
        <Col>
          <Button variant="primary">Add New Muddamaal</Button>
        </Col>
      </Row>
    </Container>
  );
};

export default Dashboard;
